import { createSelector, createFeatureSelector } from '@ngrx/store';
export const selectRestaurants = createFeatureSelector('restaurants');
export const selectCollectionState = createFeatureSelector('collection');
export const selectRestaurantCollection = createSelector(selectRestaurants, selectCollectionState, (restaurants, collection) => {
  return restaurants.filter(restaurant => collection.some(id => restaurant.id === id));
});
